const { get } = require('./dynamic-content');
const { request } = require('./constants/namespaces');

const middleware = (req, res, next) => {
  const promise = get(req, {}); // TODO - dynamic content options to disable banner
  req[request] = {
    getDynamicContent: () => promise,
  };

  next();
};

module.exports = {
  middleware,
};
