const feConfig = require('frontend-config');

const domainConfig = {
  MS: (domain) => domain,
  default: (domain, platformId, siteId, feConfigEnvironment) =>
    // eslint-disable-next-line implicit-arrow-linebreak
    feConfig.get('url.mainDomain.link', platformId, siteId, feConfigEnvironment),
};

const getDomain = (platformId, siteId, domain, feConfigEnvironment) => {
  const configHandler = domainConfig[platformId] || domainConfig.default;
  return configHandler(domain, platformId, siteId, feConfigEnvironment);
};

module.exports = {
  getDomain,
};
