const userPreferencesRequiresShowingBanner = (userPreferences) => !!(
  userPreferences
    && userPreferences.categories
    && Object.values(userPreferences.categories).some((value) => value === null)
    && (
      !Object.values(userPreferences.categories).some((value) => value === true)
      || Object.values(userPreferences.categories).some((value) => value === false)
    )
);

module.exports = {
  userPreferencesRequiresShowingBanner,
};
