const middleware = require('./middleware');
const { cookieMetricsMiddleware } = require('./cookie-metrics');

describe('http cookies middleware', () => {
  test('should be an Array', () => {
    expect(middleware).toBeInstanceOf(Array);
  });

  test('middleware contains cookieMetricsMiddleware', () => {
    expect(middleware).toEqual(
      expect.arrayContaining([
        cookieMetricsMiddleware,
      ]),
    );
  });
});
