/**
 * Supported plugins
 *
 * Each plugin must be configured with the following
 *    · module: the npm package name
 *    · propsName: the metrics configuration key that will be used to identify the plugin
 */
const supportedPlugins = [{
  module: '@frontend-metrics/hotjar'
}, {
  module: '@frontend-metrics/gtm'
}];

const getModules = () => supportedPlugins.map(plugin => plugin.module);

module.exports = {
  supportedPlugins,
  getModules
};