/**
 * Returns the local path for a specific client file
 * @param {string} script - script file name
 * @param {string} basePath - base path for the file.
 * This base path needs to take into account development/installed paths. For development, it should be
 * /modules/<navigation module>, if it is installed, it should be the name of the package installed via
 * npm, which should match the name in your package.json. For example, for ml it is @frontend-navigations/ml.
 */
const getClientSrc = (script, basePath) => script && basePath && `${basePath}/lib/client/${script}`;

module.exports = getClientSrc;
