// TODO - these are not common, should there be here?

const types = require('./types');

const isPlus = type => type === types.PLUS || type === types.FULL;
const isLite = type => type === types.LITE || type === types.LITE_ONLY;
const isInternal = type => type === types.INTERNAL;
const isHidden = type => type === types.HIDDEN;
const isClean = type => type === types.CLEAN || type === types.CLEAN_ONLY;
const isAnyPlus = type => type && type.indexOf(types.PLUS) !== -1;

module.exports = {
  isPlus,
  isLite,
  isInternal,
  isHidden,
  isClean,
  isAnyPlus,
  types,
};
