import { MobileWebkit } from "../../mobile-webkit";
import { ContextInfoResult } from "./context_info_result";
import { SessionCommands } from "./session_commands";

export class SessionCommandsImpl implements SessionCommands{

    constructor(private mobileWebkit: MobileWebkit) { }

    contextInfo(): Promise<ContextInfoResult> {
        return this.mobileWebkit.executeNative({
            method: 'context_info',
            args: {}
        });
    }
}
