import { MobileWebkit } from "../../mobile-webkit";
import { SettingsCommands } from "./settings_commands";

export class SettingsCommandsImpl implements SettingsCommands {
  constructor(private mobileWebkit: MobileWebkit) {}

  setRefreshMode(mode: "pull" | "none"): Promise<string> {
    return this.mobileWebkit.executeNative({
      method: "refresh_mode",
      args: {
        refresh_mode: mode,
      },
    });
  }
}
