import { RequestInfo } from "../request/request-info";
import { MessageToNative } from "../to-native/message-to-native";

export function requestToMessage(info:RequestInfo): MessageToNative{
    return {
        args: info.args,
        message_type: info.message_type,
        method: info.method,
        callback_msg: info.internal_data?.callback_id
    };
}