import { Platform } from "./platform";

export class IosPlatform implements Platform {
    isAvailable(): boolean {
        const win = window as any;
        return win.webkit &&
            win.webkit.messageHandlers &&
            win.webkit.messageHandlers.webviewSDKCommunicationChannel;
    }
    sendMessage(message: string) {
        (window as any).webkit.messageHandlers.webviewSDKCommunicationChannel.postMessage(message);
    }
}