import { clone } from ".";

describe("clone", () => {
    it("should clone only fieds of an object", () => {
        // Given
        const obj = { field1: 1, field2: "value", field3: false, callback: () => { } }

        // When 
        const result = clone(obj);

        // Then
        delete obj.callback;
        expect(result).toEqual(obj);
    });
});