export function isEnumValue(enumType: any, value: any): boolean {
    const enumKeys = Object.keys(enumType);
    for (let key of enumKeys) {
        if (value == enumType[key]) {
            return true;
        }
    }
    return false;
}

export function toEnum<T>(options:{enumType: any, value: any, defaultValue: T}): T {
    const {enumType, value, defaultValue} = options;
    if (isEnumValue(enumType, value)) {
        return value as T;
    }
    return defaultValue;
}
