import { findItem } from "./find-item";

describe("findItem", () => {
    it("should return an item accord a criteria", () => {
        // Given
        const data = [{ value: 1 }, { value: 2 }, { value: 3 }];

        // When
        const result = findItem(data, (item) => item.value >= 3);

        // Then
        expect(result).toEqual({ value: 3 });
    });

    it("should return undefined if criteria never match", () => {
        // Given
        const data = [{ value: 1 }, { value: 2 }, { value: 3 }];

        // When
        const result = findItem(data, (item) => item.value >= 4);

        // Then
        expect(result).toEqual(undefined);
    });
});