import { Logger } from ".";

describe("Logger", () => {
    it("should print a message with a console log", () => {
        // Given
        const message = "message";
        const logger = new Logger();
        const logSpy = jest.spyOn(console, "log");

        // When
        logger.log(message);

        // Then
        expect(logSpy).toBeCalledWith("SDK js: " + message);
    });
});