import Timer from '../lib/timer';

describe('Timer', () => {
  it('should return ellapsed milliseconds', () => {
    const DateNowMock = jest.spyOn(global.Date, 'now').mockReturnValueOnce(1).mockReturnValueOnce(2);
    const testTimer = new Timer();

    const ellapsed = testTimer.elapsedMilliseconds();
    expect(DateNowMock).toHaveBeenCalledTimes(2);
    expect(ellapsed).toEqual(1);
    DateNowMock.mockRestore();
  });
});
