const {
  get,
  getValue,
  addUpdateHandler,
} = require('../index');

/* eslint-disable no-console */

const geMLonfigurations = () => {
  console.log('get global uiNavigationVersion', get('uiNavigationVersion'));

  console.log('get ML uiNavigationVersion', get('uiNavigationVersion', 'ML'));

  console.log('get ML CBT uiNavigationVersion', get('uiNavigationVersion', 'ML', 'CBT'));

  console.log('getValue global uiNavigationVersion', getValue({
    key: 'uiNavigationVersion',
  }));

  console.log('getValue ML uiNavigationVersion', getValue({
    key: 'uiNavigationVersion',
    platform: {
      id: 'ML',
    },
  }));

  console.log('getValue ML CBT uiNavigationVersion', getValue({
    key: 'uiNavigationVersion',
    platform: {
      id: 'ML',
      siteId: 'CBT',
    },
  }));
};

geMLonfigurations();

addUpdateHandler(() => {
  console.log('Configuration updated');
  geMLonfigurations();
});
