const React = require('react');
const ShallowRenderer = require('react-test-renderer/shallow');
const Footer = require('.');

const baseProps = {
  siteId: 'CBT',
  isDesktop: true,
  isLoggedIn: true,
  links: [
    {
      link: 'http://investor.mercadolibre.com',
      name: 'Investor relations',
    },
    {
      name: 'About selling globally',
      link: 'https://portal-cbt.mercadolibre.com/about',
    },
    {
      name: 'Help',
      link: 'https://portal-cbt.mercadolibre.com/faqs',
    },
  ],
  texts: {
    copyright: 'Copyright Â© 2019 MercadoLibre S.R.L.',
  },
};

describe('CBT Navigation', () => {
  describe('Footer', () => {
    it('matches the default snapshot and returns CBT footer', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <Footer
          {...baseProps}
        />
      );

      const renderedComponent = renderer.render(component);
      expect(renderedComponent).toMatchSnapshot();
    });
  });
});
