const React = require('react');
const PropTypes = require('prop-types');
const classNames = require('classnames');
const FooterLinks = require('./links');
const Copyright = require('./copyright');
const types = require('../types');

const nameSpace = 'nav-footer-cbt';
const navFooter = 'nav-footer';

const mainClass = classNames(nameSpace, navFooter);

const Footer = ({
  links,
  texts,
}) => (
  <div className={mainClass}>
    <div className="nav-bounds">
      <FooterLinks links={links} />
      <Copyright text={texts.copyright} />
    </div>
  </div>
);

Footer.propTypes = {
  links: PropTypes.arrayOf(PropTypes.shape(types.linkType)).isRequired,
  texts: PropTypes.shape({
    copyright: PropTypes.string.isRequired,
  }).isRequired,
};

module.exports = Footer;
