const React = require('react');
const PropTypes = require('prop-types');
const FooterLink = require('./link');
const types = require('../../types');

const nameSpace = 'nav-footer-cbt';

const FooterLinks = ({ links }) => (
  <div className={`${nameSpace}__links`}>
    { links.map(link => <FooterLink text={link.name} url={link.link} />)}
  </div>
);

FooterLinks.propTypes = {
  links: PropTypes.arrayOf(PropTypes.shape(types.linkType)).isRequired,
};

module.exports = FooterLinks;
