const React = require('react');
const PropTypes = require('prop-types');
const classNames = require('classnames');

const nameSpace = 'nav-header-cbt';

/* eslint-disable nordic/no-img-tags */
const HeaderIcon = ({
  id,
  link,
  text,
  icon,
}) => (
  <div id={`${nameSpace}__icon-${id}`} className={`${nameSpace}__icon`}>
    <a href={link}>
      <img className={classNames(`${nameSpace}__icon-img`)} src={icon} alt={text} />
      { text && <div className={classNames(`${nameSpace}__icon-text`)}> { text } </div> }
    </a>
  </div>
);

HeaderIcon.defaultProps = {
  text: '',
};

HeaderIcon.propTypes = {
  text: PropTypes.string,
  link: PropTypes.string.isRequired,
  icon: PropTypes.string.isRequired,
  id: PropTypes.string.isRequired,
};

module.exports = HeaderIcon;
