const React = require('react');
const PropTypes = require('prop-types');
const HeaderLink = require('./link');
const HeaderIcon = require('./icon');
const types = require('../../../types');

const nameSpace = 'nav-header-cbt';

const HeaderLinks = ({
  links,
  icons,
}) => (
  <div className={`${nameSpace}__links`}>
    <div className={`${nameSpace}__links-titles`}>
      {
        links.map(
          (link, index) => (
            <HeaderLink text={link.name} url={link.link} id={link.id} last={(index === links.length - 1) && (!icons.length)} />
          ),
        )
      }
    </div>
    <div className={`${nameSpace}__links-icons`}>
      {
        icons.map(
          icon => (
            <HeaderIcon id={icon.id} link={icon.link} icon={icon.icon} text={icon.showText ? icon.name : ''} />
          ),
        )
      }
    </div>
  </div>
);

HeaderLinks.propTypes = {
  links: PropTypes.arrayOf(PropTypes.shape(types.linkType)).isRequired,
  icons: PropTypes.arrayOf(PropTypes.shape(types.iconType)).isRequired,
};

module.exports = HeaderLinks;
