const React = require('react');
const PropTypes = require('prop-types');

const nameSpace = 'nav-header-cbt';

const GuestMenu = ({
  logInUrl,
  logInText,
}) => (
  <div className={`${nameSpace}__guest`}>
    <a href={logInUrl} className={`${nameSpace}__login-btn`}>
      { logInText }
    </a>
  </div>
);

GuestMenu.propTypes = {
  logInText: PropTypes.string.isRequired,
  logInUrl: PropTypes.string.isRequired,
};

module.exports = GuestMenu;
