const React = require('react');
const PropTypes = require('prop-types');

const nameSpace = 'nav-header-cbt';

/* eslint-disable nordic/no-img-tags */
const LoggedInCurrentSite = (props) => {
  const { flag, finalSite, countryName } = props;
  return (
    <div className={`${nameSpace}__trigger-element ${nameSpace}__current`}>
      {flag && <div className={`${nameSpace}__current-flag`}><img src={flag} alt={countryName} /></div>}
      <div className={`${nameSpace}__current-site`}>{finalSite}</div>
    </div>);
};

LoggedInCurrentSite.defaultProps = {
  finalSite: '',
  flag: '',
  countryName: '',
};

LoggedInCurrentSite.propTypes = {
  flag: PropTypes.string,
  finalSite: PropTypes.string.isRequired,
  countryName: PropTypes.string,
};

module.exports = LoggedInCurrentSite;
