/* eslint-disable security/detect-object-injection */
const loggedInCurrentSiteProps = (props) => {
  const { currentSiteId, sites, logisticType, logisticTypes } = props;
  const currentSite = sites[currentSiteId];
  const countryId = currentSite ? currentSite.countryId : currentSiteId;
  const currentType = logisticTypes[logisticType] || '';
  const finalSite = currentType ? `${countryId} ${currentType}` : countryId;
  const flag = currentSite ? currentSite.flag : '';
  const countryName = currentSite ? currentSite.text : '';
  return {
    flag,
    finalSite,
    countryName,
  };
};

module.exports = loggedInCurrentSiteProps;
