const loggedInOptionsProps = (props) => {
  const { cbtInfo, logisticType, sites, logisticTypes, links } = props;

  return {
    myAccountText: links.myAccount.name,
    showSiteSwitch: !!(cbtInfo && cbtInfo.siteUsers && cbtInfo.siteUsers.length),
    logoutUrl: links.logout.link,
    logoutText: links.logout.name,
    cbtInfo,
    logisticType,
    sites,
    logisticTypes,
  };
};

module.exports = loggedInOptionsProps;
