const React = require('react');
const PropTypes = require('prop-types');
const classNames = require('classnames');

const nameSpace = 'nav-header-cbt';

/* eslint-disable nordic/no-img-tags */
const SiteSwitcherOption = ({
  dataValue,
  flag,
  countryName,
  site,
  selected,
}) => (
  <div
    data-value={dataValue}
    className={classNames(
      `${nameSpace}__option`, `${nameSpace}__option-switcher`, { [`${nameSpace}__option-selected`]: selected },
    )}
  >
    { flag
      && <img
        data-value={dataValue}
        src={flag}
        alt={countryName}
      />}
    { site }
  </div>
);

SiteSwitcherOption.defaultProps = {
  flag: '',
  countryName: '',
};

SiteSwitcherOption.propTypes = {
  dataValue: PropTypes.string.isRequired,
  flag: PropTypes.string,
  countryName: PropTypes.string,
  site: PropTypes.string.isRequired,
  selected: PropTypes.bool.isRequired,
};

module.exports = SiteSwitcherOption;
