const siteSwitcherOptionProps = (siteUser, last, props) => {
  const siteId = siteUser.site_id;
  const logisticType = siteUser.logistic_type;
  const siteObj = props.sites[siteId];

  const site = siteObj ? `${siteObj.text} ${props.logisticTypes[logisticType]}` : siteId;
  const flag = siteObj && siteObj.flag;
  const countryName = siteObj && siteObj.text;
  const dataValue = `${siteId}-${logisticType}`;
  const selected = siteId === props.currentSiteId && logisticType === props.logisticType;

  return {
    flag,
    countryName,
    dataValue,
    site,
    selected,
  };
};

module.exports = siteSwitcherOptionProps;
