/* eslint-disable import/no-extraneous-dependencies */
/**
 * Module dependencies
 */
const React = require('react');
const Script = require('frontend-script');
const script = require('../../client/site-switcher');

/**
 * Site Switcher Widget. This widget sets a cookie to change actual CBT site user to selected one.
 */
const SiteSwitcherWidget = ({ currentSite }) => (
  <Script>
    { `var switchSite = ${script}("${currentSite}")` }
  </Script>
);

module.exports = SiteSwitcherWidget;
