const prepareToRender = require('@frontend-navigations/commons/prepare-to-render');
const userMenuService = require('./services/user-menu');
const footerService = require('./services/footer');
const navigationPIIData = require('./services/pii');
const reqService = require('./services/req');

const services = [
  { key: 'userMenu', execute: userMenuService },
  { key: 'footer', execute: footerService },
  { key: 'navigationPIIData', execute: navigationPIIData },
  { key: 'req', execute: reqService },
];

module.exports = (props, req, res) => prepareToRender(services, props, req, res);
