const {
  get,
  getValue,
  addUpdateHandler,
} = require('../index');

/* eslint-disable no-console */

const getConfigurations = () => {
  console.log('get global uiNavigationVersion', get('uiNavigationVersion'));

  console.log('get TC uiNavigationVersion', get('uiNavigationVersion', 'TC'));

  console.log('get TC MCO uiNavigationVersion', get('uiNavigationVersion', 'TC', 'MCO'));

  console.log('getValue global uiNavigationVersion', getValue({
    key: 'uiNavigationVersion',
  }));

  console.log('getValue TC uiNavigationVersion', getValue({
    key: 'uiNavigationVersion',
    platform: {
      id: 'TC',
    },
  }));

  console.log('getValue TC MCO uiNavigationVersion', getValue({
    key: 'uiNavigationVersion',
    platform: {
      id: 'TC',
      siteId: 'MCO',
    },
  }));
};

getConfigurations();

addUpdateHandler(() => {
  console.log('Configuration updated');
  getConfigurations();
});
