const checkIfElementIsHTMLImgTag = (node) => node && node.name === 'img';

module.exports = {
  meta: {
    type: 'error',
    docs: {
      description: 'This rules checks that no <img /> tags are used in the application',
      category: 'Best Practices', // Must match one of https://eslint.org/docs/rules/
      recommended: true,
    },
    fixable: 'code',
    schema: [
    ],
    messages: {
      USE_IMAGE_COMPONENT_INSTEAD_OF_HTML_IMG:
        'Used an <img /> tag instead of the optimized <Image /> component provided by Nordic. You can learn more about this component here: http://nordic.adminml.com/docs/api/Nordic%20Modules/frontend-image',
    },
  },

  create(context) {
    return {
      JSXIdentifier(node) {
        if (checkIfElementIsHTMLImgTag(node)) {
          context.report({
            node,
            messageId: 'USE_IMAGE_COMPONENT_INSTEAD_OF_HTML_IMG',
          });
        }
      },
    };
  },
};
