module.exports = {
  meta: {
    type: 'error',
    docs: {
      description: 'This rules checks that no <script> tags are used in the application',
      category: 'Best Practices', // Must match one of https://eslint.org/docs/rules/
      recommended: true,
    },
    fixable: 'code',
    schema: [
    ],
    messages: {
      USE_FRONTEND_SCRIPT_INSTEAD_OF_TAG_SCRIPT:
      'Used an <script> tag instead of the optimized <Script /> component provided by Nordic. You can learn more about this component here: http://nordic.adminml.com/docs/api/Nordic%20Modules/frontend-script',
    },
  },

  create: function create(context) {
    return {
      'JSXOpeningElement[name.name=script]': function element(node) {
        context.report({
          node,
          messageId: 'USE_FRONTEND_SCRIPT_INSTEAD_OF_TAG_SCRIPT',
        });
      },
    };
  },
};
