module.exports = {
  meta: {
    type: 'error',
    docs: {
      description: 'This rules checks that no <style> tags are used in the application',
      category: 'Best Practices', // Must match one of https://eslint.org/docs/rules/
      recommended: true,
    },
    fixable: 'code',
    schema: [
    ],
    messages: {
      USE_FRONTEND_STYLE_INSTEAD_OF_TAG_STYLE:
      'Used an <style> tag instead of the optimized <Style /> component provided by Nordic. You can learn more about this Image component here: http://nordic.adminml.com/docs/api/Nordic%20Modules/frontend-style',
    },
  },

  create: function create(context) {
    return {
      'JSXOpeningElement[name.name=style]': function element(node) {
        context.report({
          node,
          messageId: 'USE_FRONTEND_STYLE_INSTEAD_OF_TAG_STYLE',
        });
      },
    };
  },
};
