const {
  get,
  getValue,
  addUpdateHandler,
} = require('../index');

/* eslint-disable no-console */

const getConfigurations = () => {
  console.log('get global firstParty', get('firstParty'));

  console.log('get ML firstParty', get('firstParty', 'ML'));

  console.log('get ML MLA firstParty', get('firstParty', 'ML', 'MLA'));

  console.log('getValue global firstParty', getValue({
    key: 'firstParty',
  }));

  console.log('getValue ML uiNavigationVersion', getValue({
    key: 'firstParty',
    platform: {
      id: 'ML',
    },
  }));

  console.log('getValue ML MLA firstParty', getValue({
    key: 'firstParty',
    platform: {
      id: 'ML',
      siteId: 'MLA',
    },
  }));
};

getConfigurations();

addUpdateHandler(() => {
  console.log('Configuration updated');
  getConfigurations();
});
