const env = require('frontend-env');
const statsdInternal = require('frontend-statsd/internal');

const execute = (req, res, props) => {
  if (env.PRODUCTION && env.FURY && env.APPLICATION) {
    if (props.hideCookieDisclaimerBanner) {
      statsdInternal.histogram('navigation.hide_cookie_disclaimer_banner', 1, {});
    }
    if (!(req.device || {}).webView && props.type === 'hidden') {
      statsdInternal.histogram('navigation.hidden_types', 1, {});
    }
  }
  return Promise.resolve({})
};

module.exports = { execute };
