function userMenu() {
  if (document) {
    var el = document.querySelector('.fp-nav-logged-in-menu');
    el.addEventListener('click', () => {
      var menu = document.querySelector('.fp-nav-logged-in-menu__logged-options');
      if (menu) {
        var menuDisplay = menu.style.display;
        var newStatus = menuDisplay === 'block' ? 'none' : 'block';
        menu.style.display = newStatus;
        if (newStatus === 'block') {
          document.addEventListener('click', function closeMenu(ev) {
            if (!el.contains(ev.target)) {
              menu.style.display = 'none';
              document.removeEventListener('click', closeMenu);
            }
          });
        }
      }
    }, false);
  }
}

module.exports = userMenu;
