const React = require('react');
const PropTypes = require('prop-types');

const namespace = 'fp-nav-logo-aside';

const LogoAside = ({
  text,
}) => (
  text && <span className={namespace}>{text}</span>
);

LogoAside.propTypes = {
  text: PropTypes.string.isRequired,
};

module.exports = LogoAside;

