const React = require('react');
const PropTypes = require('prop-types');
const Melilab = require('../meli-lab');

const Logo = (props) => {
  const { url, label, melilab } = props;

  const logoComponents = [
    <a className="fp-nav-logo" href={url}>{label}</a>,
  ];

  if (melilab.isEnabled) {
    logoComponents.push(<Melilab {...melilab} />);
  }

  return logoComponents;
};

Logo.propTypes = {
  url: PropTypes.string.isRequired,
  melilab: PropTypes.shape({
    isEnabled: PropTypes.bool,
  }),
  label: PropTypes.string.isRequired,
};

Logo.defaultProps = {
  melilab: {
    isEnabled: false,
  },
};

module.exports = Logo;
