const React = require('react');
const PropTypes = require('prop-types');
const LoggedInMenu = require('./logged-in-menu');
const GuestMenu = require('./guest-menu');
const loggedInMenuProps = require('./logged-in-menu/props');
const guestMenuProps = require('./guest-menu/props');
const types = require('../../types');

const namespace = 'fp-nav-user-menu';

const UserMenu = (props) => {
  const { isLoggedIn } = props;

  return (
    <div className={namespace}>
      {
        isLoggedIn
          ? <LoggedInMenu {...loggedInMenuProps(props)} />
          : <GuestMenu {...guestMenuProps(props)} />
      }
    </div>
  );
};

UserMenu.defaultProps = {
  user: null,
};

UserMenu.propTypes = {
  isLoggedIn: PropTypes.bool.isRequired,
  user: PropTypes.shape(types.userType),
  links: PropTypes.arrayOf(PropTypes.shape(types.linkType)).isRequired,
};

module.exports = UserMenu;
