const userMenuProps = (props) => {
  const {
    user,
    myAccountText,
    isLoggedIn,
    loggedOutLinks,
    loggedInLinks,
    navigationPIIData,
  } = props;
  return {
    user,
    myAccountText,
    isLoggedIn,
    links: isLoggedIn ? loggedInLinks : loggedOutLinks,
    navigationPIIData,
  };
};

module.exports = userMenuProps;
