export interface MShopsSession {
  /**
   * User ID
   */
  user_id: number;

  /**
   * Session creation date
   */
  creation_date: string;

  /**
   * Shop to which this sessions belongs to
   */
  shop_id: number;

  /**
   * Detached ID to identify the session
   * without compromising the actual session
   */
  detached_id: string;

  /**
   * Session expiration date
   */
  expire_date: string;
}

export interface MeliWebSession {
  /**
   * User ID
   */
  user_id: number;

  /**
   * If the user is a collaborator, then this shows his user ID.
   * Otherwise null.
   */
  operator_id: number | null;

  /**
   * Session creation date
   */
  creation_date: string;

  /**
   * Site ID of this session
   */
  site_id: string;

  /**
   * Scopes of this session
   */
  scopes: string[];

  /**
   * Session's signature and info
   */
  user_grant: string;

  /**
   * Detached ID to identify the session
   * without compromising the actual session
   */
  detached_id: string;

  /**
   * Root user ID.
   * A root user is the owner of different collaborators (operators)
   */
  root_id: number;

  /**
   * Session expiration date
   */
  expire_date: string;

  /**
   * If the user is an admin (impersonalized user)
   * then this is true, else is false
   */
  admin_otp: boolean;
}

export interface MeliAccessTokenSession {
  /**
   * User ID
   */
  user_id: string;

  /**
   * If the user is a collaborator, then this shows his user ID.
   * Otherwise null.
   */
  operator_id: number | null;

  /**
   * Access Token creation date
   */
  creation_date: string;

  /**
   * Site ID of this session
   */
  site_id: string;

  /**
   * Scopes of this session
   */
  scopes: string[];

  /**
   * Session's signature and info
   */
  user_grant: string;

  /**
   * Detached ID to identify the session
   * without compromising the actual session
   */
  detached_id: string;

  /**
   * Access Token created when user logged in
   */
  access_token: string;

  /**
   * Access token status
   */
  status: string;

  /**
   * Client ID representing the application where the access token was created
   */
  client_id: number;

  /**
   * User ID of the user who created the client ID corresponding to this access token
   */
  client_owner_id: number;

  /**
   * Time remaining until access token expires, in seconds
   */
  expires_in: number;

  /**
   * Indicated if this is a test access token
   */
  is_test: boolean;

  /**
   * When token is generated from a web session, that session's SSID comes here
   */
  user_session_id: string;
}

export type RequestUserSession = MeliWebSession & MeliAccessTokenSession; // Alias for retro-compatibility
