interface BaseUser {
  /**
   * User ID
   */
  id: number;

  /**
   * User's username / nickname
   */
  nickname: string;

  /**
   * If user is active or not (site_status)
   */
  active: boolean;

  /**
   * Whether this is a test user or not.
   * Will be true if user has tag 'test_user'.
   *
   * @since 4.8.0
   */
  test: boolean;

  /**
   * Users API raw response
   */
  raw: any;
}

export interface MeliUser extends BaseUser {
  /**
   * If user is an operator (collaborator)
   */
  operator: boolean;

  /**
   * If the user is a collaborator, then this shows his user ID.
   * Otherwise null.
   */
  operatorId: number | null;

  /**
   * User ID of root user (operator owner)
   *
   * @since 2.12.0
   */
  rootId: number;

  /**
   * User's scopes (permissions)
   */
  scopes: string[];

  /**
   * If user is an admin (impersonalized)
   */
  admin: boolean;

  /**
   * If user is the true owner of the account.
   * Not an admin or operator.
   */
  owner: boolean;
}

export type RequestUser = MeliUser; // Alias for retro-compatibility

export interface MshopsUser extends BaseUser {
  /**
   * Mshop ID
   *
   * @example 181748368
   * @since 5.3.0
   */
  shopId: number;

  /**
   * Mshop Name
   *
   * @example 'Norauto'
   * @since 5.3.0
   */
  shopName: string;
}
