const { existsSync } = require('fs');

const { POLYFILLS_ENTRY_NAME, POLYFILLS_FILE } = require('../../../utils/nordic-pages/helpers/constants');

const generatePolyfillsEntry = () => {
  const polyfillsEntry = {};

  if (POLYFILLS_FILE && existsSync(POLYFILLS_FILE)) {
    polyfillsEntry[POLYFILLS_ENTRY_NAME] = [POLYFILLS_FILE];
  }

  return polyfillsEntry;
};

module.exports = { generatePolyfillsEntry };
