const { CLIENT_PATH } = require('../../../../utils/nordic-pages/helpers/constants');

const getEntryByDevice = (devicePaths, hydrate = false) => {
  const { client, styles } = devicePaths;
  const clientPath = `${CLIENT_PATH}/${client}.js`;

  if (hydrate === false && !styles) return {};

  const pageEntry = { [client]: [clientPath] };

  return pageEntry;
};

module.exports = {
  getEntryByDevice,
};
