const getClientBaseError = (errorMessagePortion) => `An error occurred during the creation of the client ${errorMessagePortion}`;

const getCleanClientError = (clientPath) => 'An error occurred during the deletion of the client '
+ 'directory. Contact Nordic Team or Frontend-Core for support.\n'
+ `Client directory: ${clientPath}: \n`;

const getCreateClientError = (clientPath, error) => {
  const errorMessagePortion = `file.\nClient file: ${clientPath}\n${error}`;

  return getClientBaseError(errorMessagePortion);
};

const getCreateClientDirectoryError = (clientDirectoryPath) => {
  const errorMessagePortion = 'directory. Contact Nordic Team or Frontend-Core for support.\n'
  + `Client directory: ${clientDirectoryPath}:\n`;

  return getClientBaseError(errorMessagePortion);
};

const getInvalidHydratePropertyError = (view) => 'Invalid prop type or value for `hydrate`. '
+ 'The expected prop type is boolean: true or false. \n'
+ `File: ${view}`;

const getHydratePropertyError = (view, error) => 'An error occurred when '
+ `reading the 'hydrate' property.\nFile: ${view}\n${error}`;

module.exports = {
  getCleanClientError,
  getCreateClientError,
  getCreateClientDirectoryError,
  getInvalidHydratePropertyError,
  getHydratePropertyError,
};
