const create = require('../src/create');

const factory = (name) => create({
  data: {
    configurations: {
      uiNavigationVersion: 'some-global-value',
    },
    platforms: {
      ML: {
        configurations: {
          uiNavigationVersion: 'some-platform-value',
        },
        sites: {
          MLC: {
            uiNavigationVersion: 'some-site-value',
          },
        },
      },
    },
  },
  modulePackage: {
    name,
    version: 'production',
  },
  update: {
    intervalInMs: 4 * 60 * 1000,
  },
});

const configs = [
  'first-party-navigation-config',
  'classified-navigation-config',
  'pi-navigation-config',
  'cbt-navigation-config',
  'mp-navigation-config',
  'data-privacy-config',
  'ml-navigation-config',
];
module.exports = configs.reduce((acc, current) => ({ ...acc, [current]: factory(current) }), {});
