const path = require('path');
const { readFileSync: readFile } = require('fs');
const { template: makeTemplate } = require('lodash');

const domainCertificateSigningSource = readFile(path.join(__dirname, '../openssl-configurations/domain-certificate-signing-requests.conf'), 'utf-8');
const domainCertificateSigningConfTemplate = makeTemplate(domainCertificateSigningSource);

const domainCertificatesSource = readFile(path.join(__dirname, '../openssl-configurations/domain-certificates.conf'), 'utf-8');
const domainCertificatesConfTemplate = makeTemplate(domainCertificatesSource);

module.exports = {
  domainCertificateSigningConfTemplate,
  domainCertificatesConfTemplate,
};
