/**
 * Exports module to analize the header "Accept-Encoding" to determine if the device supports brotli compression
 * Accept-Encoding: 'gzip, deflate, br'
 * @returns Boolean
 */
module.exports = (acceptEncoding) => {
  if (typeof acceptEncoding === 'string') {
    return acceptEncoding
      .replace(/ /g, '')
      .split(',')
      .includes('br');
  }
  return false;
};
