/**
 * Detect the support of preconnect capability of the browser by using the user-agent data
 *
 * @param agent A parsed by useragent object that contains an information about the browser
 * @returns {boolean}
 */
module.exports = (agent) => {
  const browser = agent.browser.name?.toLowerCase();

  if (['opera mini', 'safari', 'mobile safari', 'ie'].indexOf(browser) !== -1) {
    return false;
  }

  if (browser === 'chrome' && agent.major < 46) {
    return false;
  }

  return true;
};
